import { createContext, useMemo, useState } from "react";
import { ThemeProvider, createTheme } from "@mui/material/styles";

export const ThemeToggleContext = createContext();
export const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#1e1e1e', // dark gray background
      paper: '#2c2c2c',    // slightly lighter for cards
    },
    primary: {
      main: '#cd7f32', // bronze for buttons
      contrastText: '#fff',
    },
    secondary: {
      main: '#cd7f32',
    },
    text: {
      primary: '#ffffff',
      secondary: '#aaaaaa',
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 6,
          fontWeight: 'bold',
        },
      },
    },
  },
});

export const lightTheme = createTheme({
  palette: {
    mode: 'light',
    background: {
      default: '#f5f5f5',
    },
    primary: {
      main: '#1976d2', // blue
    },
  },
});
