import React, { useEffect, useState } from "react";
import AddMaintenance from "./AddMaintenance";
import MaintenanceList from "./MaintenanceList";
import { auth } from "../firebase";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { Button, Box, Typography, Container } from "@mui/material";

function App() {
  const [user, setUser] = useState(null);
  const [records, setRecords] = useState([]);
  const [newlyAddedId, setNewlyAddedId] = useState(null);

  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
  }, []);

  useEffect(() => {
    const fetchRecords = async () => {
      if (!user) return;

      const token = await user.getIdToken();
      const response = await fetch("http://127.0.0.1:5000/records", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setRecords(data);
      } else {
        console.error("Failed to fetch records");
      }
    };

    fetchRecords();
  }, [user]);

  const handleLogout = async () => {
    await signOut(auth);
    setUser(null);
  };

  const handleAddRecord = (newRecord) => {
    setRecords((prev) => [...prev, newRecord]);
    setNewlyAddedId(newRecord.id);

    setTimeout(() => {
      setNewlyAddedId(null);
    }, 3000);
  };

  const handleDeleteRecord = async (id) => {
    try {
      const token = await user.getIdToken();
      const response = await fetch(`http://127.0.0.1:5000/records/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `${token}`,
        },
      });

      if (response.ok) {
        setRecords((prev) => prev.filter((record) => record.id !== id));
      } else {
        console.error("Failed to delete record");
      }
    } catch (error) {
      console.error("Error deleting record", error);
    }
  };

  if (!user) return <Typography>Please sign in</Typography>;

  return (
    <Container maxWidth="md">
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        sx={{
          backgroundColor: "#f5f5f5",
          padding: "1rem 1.5rem",
          borderRadius: "8px",
          marginBottom: "1.5rem",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <Typography variant="subtitle1" fontWeight="500">
          Hello, {user.displayName?.split(" ")[0] || "there"}
        </Typography>
        <Button variant="outlined" color="error" onClick={handleLogout}>
          Logout
        </Button>
      </Box>

      <Typography variant="h3" align="center" fontWeight="bold" mb={3}>
        ðŸš— AutoLog
      </Typography>

      <AddMaintenance onAdd={handleAddRecord} />
      <MaintenanceList
        records={records}
        newlyAddedId={newlyAddedId}
        user_id={user.uid}
        onDelete={handleDeleteRecord}
      />
    </Container>
  );
}

export default App;
