import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import MenuItem from "@mui/material/MenuItem";
import { auth } from "../firebase";
import Select from "@mui/material/Select";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Box from "@mui/material/Box";





function AddMaintenance({ user_id ,onAdd }){
    const [serviceType, setServiceType] = useState("");
    const [mileage, setMileage] = useState("");
    const [date, setDate] = useState("");

  //   const handleSubmit = async (e) => {
  //     e.preventDefault();
  //     if (!serviceType || !mileage || !date) return;
  //     const user = auth.currentUser;
  //     if(!user) {
  //       console.error("User is not logged in");
  //       return;
  //     }

  //     const idToken = await user.getIdToken();
  
  //     const newRecord = { serviceType, mileage, date, user_id };
      
        

  
  //     try {
  //         console.log("Submitting record:", { serviceType, mileage, date });
  //         const response = await fetch("http://127.0.0.1:5000/add_record", {
  //             method: "POST",
  //             headers: { 
  //               "Content-Type": "application/json",
  //             },
  //             body: JSON.stringify(newRecord)
  //         });
  
  //         if (!response.ok) {
  //             throw new Error("Failed to add record");
  //         }
  
  //         const data = await response.json();
  //         console.log("Record Added Successfully!", data);
  //         window.location.reload();
  
  //     } catch (error) {
  //         console.error("Error:", error);
  //     }
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!serviceType || !mileage || !date) return;
  
    const user = auth.currentUser;
    if (!user) {
      console.error("User is not logged in");
      return;
    }
  
    const idToken = await user.getIdToken();
  
    try {
      const response = await fetch("http://127.0.0.1:5000/add_record", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `${idToken}`, // ✅ token included
        },
        body: JSON.stringify({
          serviceType,
          mileage,
          date,
        }),
      });
  
      if (!response.ok) throw new Error("Failed to add record");
      const data = await response.json();
      console.log("Added record:", data);
      window.location.reload();
    } catch (error) {
      console.error("Error:", error);
    }
  };
  
  

    


    return (
       <Box
  sx={{
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: 2, 
    width: "100%",
    maxWidth: "400px", 
    margin: "auto",
  }}
>
  {/* Service Type Dropdown */}
  <TextField
    select
    fullWidth
    label="Service Type"
    value={serviceType}
    onChange={(e) => setServiceType(e.target.value)}
    variant="outlined"
  >
    <MenuItem value="Oil Change">Oil Change</MenuItem>
    <MenuItem value="Brake Pads">Brake Pads</MenuItem>
    <MenuItem value="Tire Rotation">Tire Rotation</MenuItem>
    <MenuItem value="Battery Replacment">Battery Replacment</MenuItem>
    <MenuItem value="Spark Plugs">Spark Plugs</MenuItem>
    <MenuItem value="Coolant Flush">Coolant Flush</MenuItem>
    <MenuItem value="Transmission Fluid">Transmission Fluid</MenuItem>
    <MenuItem value="Cabin Air Filter">Cabin Air Filter</MenuItem>
    <MenuItem value="Inspection">Inspection</MenuItem>
    <MenuItem value="Alignment">Alignment</MenuItem>
    <MenuItem value="Tire Change">Tire Change</MenuItem>
    <MenuItem value="Power Steering Fluid">Power Steering Fluid</MenuItem>
    <MenuItem value="Serpentine Belt">Serpentine Belt</MenuItem>
    <MenuItem value="Wiper Blades">Wiper Blades</MenuItem>
    <MenuItem value="Brake Fluid">Brake Fluid</MenuItem>
    <MenuItem value="Engine Replacment">Engine Replacment</MenuItem>
    <MenuItem value="Transmission Replacment">Transmission Replacment</MenuItem>
    <MenuItem value="Brake Caliper Replacment">Brake Caliper Replacment</MenuItem>
    <MenuItem value="Strut Replacment">Strut Replacment</MenuItem>
    <MenuItem value="Wheel Balance">Wheel Balance</MenuItem>
    <MenuItem value="Alternator Replacment">Altermator Replacment</MenuItem>
    <MenuItem value="Engine Air Filter Replacment">Engine Air Filter Replacment</MenuItem>
    <MenuItem value="Headlight Bulb Replacment">Headlight Bulb Replacment</MenuItem>
    <MenuItem value="Wildsheild Crack Repair">Windsheild Crack Repair</MenuItem>
    <MenuItem value="Windsheild Replacment">Windsheild Replacment</MenuItem>






  </TextField>

  {/* Mileage Input */}
  <TextField
    fullWidth
    type="number"
    label="Mileage"
    value={mileage}
    onChange={(e) => setMileage(e.target.value)}
    variant="outlined"
  />

  {/* Date Input */}
  <TextField
    fullWidth
    slotProps={{ inputLabel: {shrink: true} }}
    type="date"
    label="Date"
    value={date}
    
    onChange={(e) => setDate(e.target.value)}
    variant="outlined"
  />

  {/* Submit Button */}
  <Button
    variant="contained"
    color="primary"
    sx={{ width: "100%", fontSize: "16px", padding: "10px", borderRadius: "8px" }}
    onClick={handleSubmit}

  >
    ADD MAINTENANCE RECORD
  </Button>
</Box>
        
      );
        
      
    }

    
    
    export default AddMaintenance;
