import React, { useState, useEffect, useContext } from "react";
import AddMaintenance from "./components/AddMaintenance";
import MaintenanceList from "./components/MaintenanceList";
import Login from "./components/Login";
import { auth } from "./firebase";
import {
  Box,
  Button,
  CssBaseline,
  Typography,
  Switch,
  FormControlLabel,
  ThemeProvider,
  GlobalStyles,
} from "@mui/material";
import { ThemeToggleContext, lightTheme, darkTheme } from "./ThemeContext";

function App() {
  const [user, setUser] = useState(null);
  const [records, setRecords] = useState([]);
  const [newlyAddedId, setNewlyAddedId] = useState(null);
  const { mode, setMode } = useContext(ThemeToggleContext);
  const theme = mode === "dark" ? darkTheme : lightTheme;

  // âœ… Read mode from localStorage on first load
  useEffect(() => {
    const savedMode = localStorage.getItem("themeMode");
    if (savedMode) {
      setMode(savedMode);
    }
  }, [setMode]);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
    });
    return unsubscribe;
  }, []);

  const handleLogout = () => auth.signOut();

  const handleAddRecord = (newRecord) => {
    setRecords((prev) => [...prev, newRecord]);
    setNewlyAddedId(newRecord.id);
  };

  const handleDeleteRecord = (id) => {
    setRecords((prev) => prev.filter((record) => record.id !== id));
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <GlobalStyles
        styles={{
          body: {
            margin: 0,
            backgroundColor: theme.palette.background.default,
          },
          "#root": {
            backgroundColor: theme.palette.background.default,
            minHeight: "100vh",
          },
        }}
      />
      <Box sx={{ p: 2 }}>
        <Typography
          variant="h4"
          align="center"
          fontWeight="bold"
          sx={{
            color: theme.palette.primary.main,
            mb: 3,
          }}
        >
          ðŸš— AutoLog
        </Typography>

        {user ? (
          <>
            <Box
              display="flex"
              justifyContent="space-between"
              alignItems="center"
              sx={{
                backgroundColor: "background.paper",
                padding: "1rem 1.5rem",
                borderRadius: "8px",
                marginBottom: "1.5rem",
                boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              }}
            >
              <Typography variant="subtitle1" fontWeight="500">
                Hello, {user.displayName?.split(" ")[0] || "there"}
              </Typography>

              <Box display="flex" alignItems="center" gap={2}>
                <FormControlLabel
                  control={
                    <Switch
                      checked={mode === "dark"}
                      onChange={() =>
                        setMode((prev) => {
                          const next = prev === "light" ? "dark" : "light";
                          localStorage.setItem("themeMode", next);
                          return next;
                        })
                      }
                      color="primary"
                    />
                  }
                  label={mode === "dark" ? "Dark" : "Light"}
                />
                <Button variant="outlined" color="error" onClick={handleLogout}>
                  Logout
                </Button>
              </Box>
            </Box>

            <AddMaintenance user_id={user.uid} user={user} onAdd={handleAddRecord} />
            <MaintenanceList
              records={records}
              newlyAddedId={newlyAddedId}
              user_id={user.uid}
              onDelete={handleDeleteRecord}
            />
          </>
        ) : (
          <Login onLogin={setUser} />
        )}
      </Box>
    </ThemeProvider>
  );
}

export default App;
