import React, { useState, useEffect } from "react";
import { auth, signOut } from "./firebase";
import Login from "./components/Login";
import AddMaintenance from "./components/AddMaintenance";
import MaintenanceList from "./components/MaintenanceList";
import { Button, Box, Typography } from "@mui/material";


function App() {
  const [user, setUser] = useState(null);
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newlyAddedId, setNewlyAddedId] = useState(null);

  const handleAddRecord = (newRecord) => {
    setRecords((prevRecords) => [...prevRecords, newRecord]); // ✅ add it to list
    setNewlyAddedId(newRecord.id);                             // ✅ highlight it

    setTimeout(() => {
      setNewlyAddedId(null);
    }, 3000);
  };



  // Load user from localStorage
  useEffect(() => {
    const storedUserId = localStorage.getItem("user_id");
    const storedUserEmail = localStorage.getItem("userEmail");

    if (storedUserId) {
      setUser({ uid: storedUserId, email: storedUserEmail });
    }

    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        console.log("User logged in:", user.uid);
        setUser(user);
        localStorage.setItem("user_id", user.uid);
        localStorage.setItem("userEmail", user.email);
      } else {
        console.log("No user found, clearing storage.");
        setUser(null);
        localStorage.removeItem("user_id");
        localStorage.removeItem("userEmail");
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  // Fetch records for the logged in user
  // const fetchRecords = async () => {
  //   if (!user || !user.uid) {
  //     console.log("User ID is missing, skipping API call.");
  //     return;
  //   }

  //   console.log("Fetching records for user ID:", user.uid);

  //   try {
  //     const response = await fetch(`http://127.0.0.1:5000/records?user_id=${user.uid}`);
  //     if (!response.ok) {
  //       throw new Error("Failed to fetch data from database");
  //     }
  //     const data = await response.json();
  //     setRecords(data);
  //   } catch (error) {
  //     console.error("Error fetching records:", error);
  //   }
  // };

  const fetchRecords = async () => {
    if (!user) return;
    try {
      const token = await user.getIdToken();
      const response = await fetch(`http://127.0.0.1:5000/records?user_id=${user.uid}`, {
        headers: {
          Authorization: `${token}`,
        },
      });

      const data = await response.json();
      setRecords(data);
    } catch (error) {
      console.error("Error fetching records:", error);
    }
  };


  useEffect(() => {
    if (user && user.uid) {
      fetchRecords();
    }
  }, [user]);

  const handleLogout = async () => {
    await signOut(auth);
    setUser(null);
    setRecords([]);
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <Typography
        variant="h3"
        align="center"
        fontWeight="bold"
        sx={{
          color: "#1565c0",  // deep blue
          textShadow: "1px 1px 3px rgba(0,0,0,0.1)",
          letterSpacing: "1px",
          mb: 3,
        }}
      >
🚗 <strong>AutoLog</strong>

</Typography>
      {user ? (
        <>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            sx={{
              backgroundColor: "#f5f5f5",
              padding: "1rem 1.5rem",
              borderRadius: "8px",
              marginBottom: "1.5rem",
              boxShadow: "0 2px 6px rgba(77, 72, 243, 0.1)",
            }}
          >
            <Typography variant="subtitle1" fontWeight="500" color="primary">
              Welcome, {user.displayName?.split(" ")[0] || "there"}
            </Typography>
            <Button variant="outlined" color="primary" onClick={handleLogout}>
              Logout
            </Button>
          </Box>

          <AddMaintenance user_id={user.uid} onAdd={handleAddRecord} />
          <MaintenanceList
            records={records}
            newlyAddedId={newlyAddedId}
            user_id={user.uid}
          />
        </>
      ) : (
        <Login onLogin={setUser} />
      )}
    </div>
  );
}

export default App;
