import React, { useState, useEffect, useContext } from "react";
import { auth, signOut } from "./firebase";
import Login from "./components/Login";
import AddMaintenance from "./components/AddMaintenance";
import MaintenanceList from "./components/MaintenanceList";
import {
  Button,
  Box,
  Typography,
  Switch,
  FormControlLabel,
  useTheme,
} from "@mui/material";
import { ThemeToggleContext } from "./ThemeContext";

function App() {
  const [user, setUser] = useState(null);
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newlyAddedId, setNewlyAddedId] = useState(null);

  const { mode, setMode } = useContext(ThemeToggleContext);
  const theme = useTheme(); // ðŸŸ© Access current theme colors

  const handleAddRecord = (newRecord) => {
    setRecords((prevRecords) => [...prevRecords, newRecord]);
    setNewlyAddedId(newRecord.id);

    setTimeout(() => {
      setNewlyAddedId(null);
    }, 3000);
  };

  useEffect(() => {
    const storedUserId = localStorage.getItem("user_id");
    const storedUserEmail = localStorage.getItem("userEmail");

    if (storedUserId) {
      setUser({ uid: storedUserId, email: storedUserEmail });
    }

    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        setUser(user);
        localStorage.setItem("user_id", user.uid);
        localStorage.setItem("userEmail", user.email);
      } else {
        setUser(null);
        localStorage.removeItem("user_id");
        localStorage.removeItem("userEmail");
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const fetchRecords = async () => {
    if (!user) return;
    try {
      const token = await user.getIdToken();
      const response = await fetch(`http://127.0.0.1:5000/records?user_id=${user.uid}`, {
        headers: { Authorization: `${token}` },
      });
      const data = await response.json();
      setRecords(data);
    } catch (error) {
      console.error("Error fetching records:", error);
    }
  };

  const handleDeleteRecord = async (id) => {
    try {
      const token = await user.getIdToken();
      const response = await fetch(`http://127.0.0.1:5000/records/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        setRecords((prev) => prev.filter((record) => record.id !== id));
      } else {
        console.error("Failed to delete record from backend");
      }
    } catch (error) {
      console.error("Error deleting record:", error);
    }
  };

  useEffect(() => {
    if (user && user.uid) {
      fetchRecords();
    }
  }, [user]);

  const handleLogout = async () => {
    await signOut(auth);
    setUser(null);
    setRecords([]);
  };

  if (loading) return <p>Loading...</p>;

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundColor: mode === "dark" ? "#121212" : "#f5f5f5",
      }}
    >
      <Typography
        variant="h4"
        fontWeight="bold"
        align="center"
        gutterBottom
        sx={{ color: mode === "dark" ? "#D87C35" : "primary.main" }}
      >
        ðŸš— AutoLog
      </Typography>

      {user ? (
        <>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            sx={{
              backgroundColor: "background.paper",
              padding: "1rem 1.5rem",
              borderRadius: "8px",
              marginBottom: "1.5rem",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
            }}
          >
            <Typography variant="subtitle1" fontWeight="500">
              Hello, {user.displayName?.split(" ")[0] || "there"}
            </Typography>

            <Box display="flex" alignItems="center" gap={2}>
              <FormControlLabel
                control={
                  <Switch
                    checked={mode === "dark"}
                    onChange={() =>
                      setMode((prev) => (prev === "light" ? "dark" : "light"))
                    }
                    color="primary"
                  />
                }
                label={mode === "dark" ? "Dark" : "Light"}
              />
              <Button variant="outlined" color="error" onClick={handleLogout}>
                Logout
              </Button>
            </Box>
          </Box>

          <AddMaintenance user_id={user.uid} onAdd={handleAddRecord} />

          <MaintenanceList
            records={records}
            newlyAddedId={newlyAddedId}
            user_id={user.uid}
            onDelete={handleDeleteRecord}
          />
        </>
      ) : (
        <Login onLogin={setUser} />
      )}
    </Box>
  );

}

export default App;
