import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Box from "@mui/material/Box";




function AddMaintenance({ user_id ,onAdd }){
    const [serviceType, setServiceType] = useState("");
    const [mileage, setMileage] = useState("");
    const [date, setDate] = useState("");

    const handleSubmit = async (e) => {
      e.preventDefault();
      if (!serviceType || !mileage || !date) return;
  
      const newRecord = { user_id: user_id, serviceType, mileage, date };
  
      try {
          console.log("Submitting record:", { user_id: user_id, serviceType, mileage, date });
          const response = await fetch("http://127.0.0.1:5000/add_record", {
              method: "POST",
              headers: { 
                "Content-Type": "application/json",
              },
              body: JSON.stringify(newRecord)
          });
  
          if (!response.ok) {
              throw new Error("Failed to add record");
          }
  
          const data = await response.json();
          console.log("Record Added Successfully!", data);
          window.location.reload();
  
      } catch (error) {
          console.error("Error:", error);
      }
  };
  

    


    return (
       <Box
  sx={{
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: 2, 
    width: "100%",
    maxWidth: "400px", 
    margin: "auto",
  }}
>
  {/* Service Type Dropdown */}
  <TextField
    select
    fullWidth
    label="Service Type"
    value={serviceType}
    onChange={(e) => setServiceType(e.target.value)}
    variant="outlined"
  >
    <MenuItem value="Oil Change">Oil Change</MenuItem>
    <MenuItem value="Brake Pads">Brake Pads</MenuItem>
    <MenuItem value="Tire Rotation">Tire Rotation</MenuItem>
  </TextField>

  {/* Mileage Input */}
  <TextField
    fullWidth
    type="number"
    label="Mileage"
    value={mileage}
    onChange={(e) => setMileage(e.target.value)}
    variant="outlined"
  />

  {/* Date Input */}
  <TextField
    fullWidth
    slotProps={{ inputLabel: {shrink: true} }}
    type="date"
    label="Date"
    value={date}
    
    onChange={(e) => setDate(e.target.value)}
    variant="outlined"
  />

  {/* Submit Button */}
  <Button
    variant="contained"
    color="primary"
    sx={{ width: "100%", fontSize: "16px", padding: "10px", borderRadius: "8px" }}
    onClick={handleSubmit}

  >
    ADD MAINTENANCE RECORD
  </Button>
</Box>
        
      );
        
      
    }

    
    
    export default AddMaintenance;
