import React, { useState } from "react";
import { Autocomplete, TextField } from "@mui/material";
import Button from "@mui/material/Button";
import MenuItem from "@mui/material/MenuItem";
import { auth } from "../firebase";
import Select from "@mui/material/Select";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";






function AddMaintenance({ user_id ,onAdd }){
    const [serviceType, setServiceType] = useState("");
    const [mileage, setMileage] = useState("");
    const [date, setDate] = useState("");
    const maintenanceOptions = [
      "Oil Change",
      "Brake Pads",
      "Tire Rotation",
      "Battery Replacement",
      "Spark Plugs",
      "Coolant Flush",
      "Transmission Fluid",
      "Cabin Air Filter",
      "Inspection",
      "Alignment",
      "Tire Change",
      "Power Steering Fluid",
      "Serpentine Belt",
      "Wiper Blades",
      "Brake Fluid",
      "Engine Replacement",
      "Transmission Replacement",
      "Brake Caliper Replacement",
      "Strut Replacement",
      "Wheel Balance",
      "Alternator Replacement",
      "Engine Air Filter Replacement",
      "Headlight Bulb Replacement",
      "Windshield Crack Repair",
      "Windshield Replacement",
      "Custom..."
    ];

  //   const handleSubmit = async (e) => {
  //     e.preventDefault();
  //     if (!serviceType || !mileage || !date) return;
  //     const user = auth.currentUser;
  //     if(!user) {
  //       console.error("User is not logged in");
  //       return;
  //     }

  //     const idToken = await user.getIdToken();
  
  //     const newRecord = { serviceType, mileage, date, user_id };
      
        

  
  //     try {
  //         console.log("Submitting record:", { serviceType, mileage, date });
  //         const response = await fetch("http://127.0.0.1:5000/add_record", {
  //             method: "POST",
  //             headers: { 
  //               "Content-Type": "application/json",
  //             },
  //             body: JSON.stringify(newRecord)
  //         });
  
  //         if (!response.ok) {
  //             throw new Error("Failed to add record");
  //         }
  
  //         const data = await response.json();
  //         console.log("Record Added Successfully!", data);
  //         window.location.reload();
  
  //     } catch (error) {
  //         console.error("Error:", error);
  //     }
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!serviceType || !mileage || !date) return;
  
    const user = auth.currentUser;
    if (!user) {
      console.error("User is not logged in");
      return;
    }
  
    const idToken = await user.getIdToken();
  
    try {
      const response = await fetch("http://127.0.0.1:5000/add_record", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `${idToken}`, // ✅ token included
        },
        body: JSON.stringify({
          serviceType,
          mileage,
          date,
        }),
      });
  
      if (!response.ok) throw new Error("Failed to add record");
      const data = await response.json();
      console.log("Added record:", data);
      window.location.reload();
    } catch (error) {
      console.error("Error:", error);
    }
  };
  
  

  const today = new Date().toISOString().split("T")[0];


  return (
    <Box
      sx={{
        maxWidth: "600px",
        margin: "auto",
        marginTop: "20px",
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "8px",
        backgroundColor: "#f9f9f9",
      }}
    >
      <Typography variant="h6" fontWeight="bold" gutterBottom>
        Add Maintenance Record
      </Typography>
      <form onSubmit={handleSubmit}>
        <Autocomplete
          freeSolo
          fullWidth
          options={maintenanceOptions}
          value={serviceType}
          onChange={(event, newValue) => setServiceType(newValue)}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Service Type"
              variant="outlined"
              required
              margin="normal"
            />
          )}
        />
        <TextField
          fullWidth
          label="Mileage"
          type="number"
          value={mileage}
          onChange={(e) => setMileage(e.target.value)}
          variant="outlined"
          required
          margin="normal"
          slotProps={{
            min: 0,
            max: 1000000
          }}
        />
        <TextField
          fullWidth
          label="Date"
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          InputLabelProps={{ shrink: true }}
          variant="outlined"
          required
          margin="normal"
          slotProps={{
            max: today
          }}
        />
        <Button
          fullWidth
          type="submit"
          variant="contained"
          color="primary"
          sx={{ marginTop: "16px" }}
        >
          Add Record
        </Button>
      </form>
    </Box>
  );
}

export default AddMaintenance;
