import React, { useEffect, useState, useContext } from "react";
import { CssBaseline, Box, Button, Typography, Switch, FormControlLabel, ThemeProvider, GlobalStyles } from "@mui/material";
import AddMaintenance from "./components/AddMaintenance";
import MaintenanceList from "./components/MaintenanceList";
import Login from "./components/Login";

import { auth } from "./firebase";
import { ThemeToggleContext, darkTheme, lightTheme } from "./ThemeContext";

function App() {
  const [user, setUser] = useState(null);
  const [records, setRecords] = useState([]);
  const [newlyAddedId, setNewlyAddedId] = useState(null);
  const { mode, setMode } = useContext(ThemeToggleContext);
  const theme = mode === "dark" ? darkTheme : lightTheme;

  const handleAddRecord = async (newRecord) => {
    try {
      const token = await user.getIdToken(); // âœ… get token

      const response = await fetch("http://127.0.0.1:5000/add_record", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`, // âœ… include token
        },
        body: JSON.stringify({ ...newRecord, user_id: user.uid }),
      });

      if (response.ok) {
        fetchRecords(); // âœ… refresh the list after adding
      } else {
        const error = await response.json();
        console.error("Failed to add record:", error);
      }
    } catch (error) {
      console.error("Error adding record:", error);
    }
  };


  const handleDeleteRecord = async (id) => {
    try {
      const token = await user.getIdToken();
      const response = await fetch(`http://127.0.0.1:5000/records/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error("Failed to delete record");

      setRecords((prev) => prev.filter((record) => record.id !== id));
    } catch (error) {
      console.error("Delete error:", error);
    }
  };


  const fetchRecords = async (userId) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/records?user_id=${userId}`);
      const data = await response.json();
      setRecords(data);
    } catch (err) {
      console.error("Error fetching records:", err);
    }
  };



  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(async (user) => {
      if (user) {
        setUser(user);
        fetchRecords(user);
      } else {
        setUser(null);
        setRecords([]);
      }
    });
    return () => unsubscribe();
  }, []);

  useEffect(() => {
    const savedMode = localStorage.getItem("themeMode");
    if (savedMode) setMode(savedMode);
  }, [setMode]);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <GlobalStyles
        styles={{
          body: {
            margin: 0,
            backgroundColor: theme.palette.background.default,
          },
          "#root": {
            backgroundColor: theme.palette.background.default,
            minHeight: "100vh",
          },
        }}
      />
      <Box sx={{ p: 2 }}>
        <Typography variant="h4" align="center" fontWeight="bold" sx={{ color: theme.palette.primary.main, mb: 3 }}>
          ðŸš— AutoLog
        </Typography>

        {user ? (
          <>
            <Box
              display="flex"
              justifyContent="space-between"
              alignItems="center"
              sx={{
                backgroundColor: "background.paper",
                padding: "1rem 1.5rem",
                borderRadius: "8px",
                marginBottom: "1.5rem",
                boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              }}
            >
              <Typography variant="subtitle1" fontWeight="500">
                Hello, {user.displayName?.split(" ")[0] || "there"}
              </Typography>

              <Box display="flex" alignItems="center" gap={2}>
                <FormControlLabel
                  control={
                    <Switch
                      checked={mode === "dark"}
                      onChange={() =>
                        setMode((prev) => {
                          const next = prev === "light" ? "dark" : "light";
                          localStorage.setItem("themeMode", next);
                          return next;
                        })
                      }
                      color="primary"
                    />
                  }
                  label={mode === "dark" ? "Dark" : "Light"}
                />
                <Button variant="outlined" color="error" onClick={() => auth.signOut()}>
                  Logout
                </Button>
              </Box>
            </Box>

            <AddMaintenance user={user} user_id={user.uid} onAdd={handleAddRecord} />
            <MaintenanceList records={records} user_id={user.uid} onDelete={handleDeleteRecord} newlyAddedId={newlyAddedId} />
          </>
        ) : (
          <Login onLogin={setUser} />
        )}
      </Box>
    </ThemeProvider>
  );
}

export default App;
