import React, { useState, useEffect } from "react";
import Login from "./components/Login";  // ✅ Correct import
import { auth, signOut } from "./firebase";
import AddMaintenance from "./components/AddMaintenance";
import MaintenanceList from "./components/MaintenanceList";

function App() {
    const [records, setRecords] = useState([]);
    const [user, setUser] = useState(null);

    const handleLogin = (uid, email) => {
        localStorage.setItem("userId", uid);
        localStorage.setItem("userEmail", email);
        setUser({ uid, email });
    };

    const handleLogout = async () => {
        try {
            await signOut(auth);
            localStorage.removeItem("userId");
            localStorage.removeItem("userEmail");
            setUser(null);
        } catch (error) {
            console.error("Logout Error", error);
        }
    };

    const fetchRecords = async () => {
        try {
            const response = await fetch("http://127.0.0.1:5000/records");
            const data = await response.json();
            setRecords(data);
        } catch (error) {
            console.error("Error fetching records:", error);
        }
    };

    useEffect(() => {
        fetchRecords();
    }, []);

    return (
        <div>
            <h1>Car Maintenance Tracker</h1>
            {user ? (
                <>
                    <button onClick={handleLogout}>Logout</button>
                    <AddMaintenance onAdd={fetchRecords} />
                    <MaintenanceList userId={user.uid} />
                </>
            ) : (
                <Login onLogin={handleLogin} />
            )}
        </div>
    );
}

export default App;
