import React from "react";
import Card from "@mui/material/Card";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import CardContent from "@mui/material/CardContent";

const deleteRecord = async (id) => {
        try {
          const response = await fetch(`http://127.0.0.1:5000/records/${id}`, {
            
            method: "DELETE",
          });
  
          if(response.ok){
            setRecords(records.filter(record => record.id !== id)); // Remove from UI
          }else{
            console.error("Failed to delete record");
          }
        }catch (error) {
          console.error("Error deleting record", error);
        }
      };

function MaintenanceList({ records, newlyAddedId, onDelete }) {
  return (
    <Box sx={{ maxWidth: "600px", margin: "auto", marginTop: "20px" }}>
      <Typography variant="h5" fontWeight="bold" gutterBottom>
        Maintenance History
      </Typography>

      {records.length === 0 ? (
        <Typography color="textSecondary">No records found.</Typography>
      ) : (
        records.map((record) => (
          <Card
            key={record.id}
            sx={{
              marginBottom: "10px",
              padding: "10px",
              borderRadius: "8px",
              backgroundColor:
                record.id === newlyAddedId ? "#d0f5d8" : "#f9f9f9",
              transition: "background-color 0.5s ease",
              borderLeft: "5px solid #1976d2",
            }}
          >
            <CardContent>
              <Typography variant="h6" fontWeight="bold">
                {record.service_type}
              </Typography>
              <Typography variant="body2" color="textSecondary">
                üìÖ {record.date} | üöó {record.mileage} miles
              </Typography>
            </CardContent>
            <Button
              variant="contained"
              color="primary"
              onClick={() => deleteRecord(record.id)}
            >
              üóëÔ∏è
            </Button>
          </Card>
        ))
      )}
    </Box>
  );
}

export default MaintenanceList;
